let passwordValidator = require('password-validator');

// Create a schema
let schema = new passwordValidator();

// Add properties to it
schema
  .is().min(8)                                    // Minimum length 8
  .is().max(100)                                  // Maximum length 100
  .has().uppercase()                              // Must have uppercase letters
  .has().lowercase()                              // Must have lowercase letters
  .has().digits(2)                                // Must have at least 2 digits
  .has().symbols(1);                          // Should not have spaces

const checkPassword = () => {
  document.addEventListener('change', (_event) => {
    const password = document.getElementById('user_password').value;
    checkUppercase();
    console.log(password);
    console.log(schema.validate(password, { list: true }));
  });
};

const checkUppercase = (list) => {
  const lowerValidation = document.getElementById('lower-password');
  if (list.include('lowercase')) {
    lowerValidation.innerHTML = "❌ Contains lowercase";
  } else {
    lowerValidation.innerHTML = "✅ Contains lowercase";
  }
}

export { checkPassword };
